import { useState } from 'react'
import { useDispatch } from 'react-redux'
import {v4 as uuidv4 }  from 'uuid'

function Add() {

    const [desc, setDesc] = useState('');
    const [amount, setAmount] = useState(0);
    const [addType, setAddType] = useState(true);
    

    const dispatch = useDispatch();

    const handleAdd = () => {
        dispatch({
            type: "budget/add",
            id: uuidv4(),
            addType: '+',
            desc,
            amount
        })
    }

    const handleDesc = (e) => {
        setDesc(e.target.value)
    }

    const handleAmount = (e) => {
        setAmount(e.target.value)
    }

    return (
        <div className="add">
            <div className="add__container">
                <select className="add__type">
                    <option value="inc">+</option>
                    <option value="exp">-</option>
                </select>
                <input type="text" className="add__description" placeholder="Add description" onChange={handleDesc} />
                <input type="number" className="add__value" placeholder="Value" onChange={handleAmount} />
                <button className="add__btn" onClick={handleAdd}><i className="ion-ios-checkmark-outline" /></button>
            </div>
        </div>
    )
}

export default Add
