import { useState } from 'react'
import { useDispatch } from 'react-redux'

function Add() {

    const [value, setValue] = useState('');

    const dispatch = useDispatch();

    const handleAdd = () => {
        dispatch(
			addItem({
				task: value
			})
		);

        setValue('')
    }

    return (
        <div className="add">
            <div className="add__container">
                <select className="add__type">
                    <option value="inc">+</option>
                    <option value="exp">-</option>
                </select>
                <input type="text" className="add__description" placeholder="Add description" />
                <input type="number" className="add__value" placeholder="Value" />
                <button className="add__btn" onClick={handleAdd}><i className="ion-ios-checkmark-outline" /></button>
            </div>
        </div>
    )
}

export default Add
