import { useState } from 'react'
import { useDispatch } from 'react-redux'
import {v4 as uuidv4 }  from 'uuid'

function Add() {

    const [desc, setDesc] = useState('');
    const [amount, setAmount] = useState('');
    const [addType, setAddType] = useState('inc');
    

    const dispatch = useDispatch();

    const handleAdd = () => {
        dispatch({
            type: 'budget/add',
            id: uuidv4(),
            addType,
            desc,
            amount
        })
    }

    const handleDesc = (e) => {
        setDesc(e.target.value)
    }

    const handleAddType = (e) => {
        e.target.value === 'inc' ? setAddType('inc') : setAddType('exp')
    }

    // const handleAmount = (e) => {
    //     addType === 'inc' ? setAmount(e.target.value) : setAmount(-e.target.value)
    // }

    const handleAmount = (e) => {
        setAmount(e.target.value)
    }

    // console.log(addType)
    

    return (
        <div className="add">
            <div className="add__container">
                <select className="add__type" onChange={handleAddType}>
                    <option value="inc">+</option>
                    <option value="exp">-</option>
                </select>
                <input type="text" className="add__description" placeholder="Add description" onChange={handleDesc} />
                {/* <input type="number" className="add__value" placeholder="Value" onChange={handleAmount} /> */}
                {/* <input type="number" className="add__value" placeholder="Value" /> */}
                <button className="add__btn" onClick={handleAdd}><i className="ion-ios-checkmark-outline" /></button>
            </div>
        </div>
    )
}

export default Add
