// import store  from "./store"

const initValue = {
    addOrEdit: 'add',
    listData: [
        // {
        //     id: 1,
        //     desc: 'Buy',
        //     amount: 300
        // },
        // {
        //     id: 2,
        //     desc: 'Buy 2',
        //     amount: -400
        // },
    ]
}

function reducer(state = initValue, action) {

    switch (action.type) {
        case 'budget/add': {
            return {
                ...state,
                listData: [
                    ...state.listData,
                    {
                        id: action.id,
                        addType: action.addType,
                        desc: action.desc,
                        amount: action.addType === 'inc' ? action.amount*1 : action.amount*(-1) 
                    }
                ]
            }
        }
        case 'budget/delete': {
            return {
                ...state,
                listData: [
                    // ...state.listData,
                    ...state.listData.filter((item) => item.id !== action.id)
                ]  
            }
        }
        case 'budget/edit': {
            return {
                ...state,
                listData: [
                    // ...state.listData,
                    ...state.listData.filter((item) => item.id !== action.id)
                ]  
            }
        }

        default: return state
    }
}



export default reducer
