import { createSlice } from '@reduxjs/toolkit'

export const addSlice = createSlice({
    name: "add",
    initialState: {
        status: true,
        desc: 'hi redux',
    },
    reducers: {
        changeStatus: (state) => {
            state.status = !state.status
        }
    }
})
