import { legacy_createStore } from 'redux'
import reducer from './reducer'


const store = legacy_createStore(reducer)

// Testing

const initValue = {
    listData: [
        {
            id: 1,
            desc: 'Buy',
            amount: 300
        },
        {
            id: 2,
            desc: 'Buy 2',
            amount: -400
        },
    ]
}

const testAdd = {
    type: 'budget/add',
    id: 3,
    desc: 'Buy 3',
    amount: 500
}



console.log('Test add', store.dispatch())
console.log('Get state', store.getState())


export default store
