const initValue = {
    listData: [
        {
            id: 1,
            desc: 'Buy',
            amount: 300
        },
        {
            id: 2,
            desc: 'Buy 2',
            amount: -400
        },
    ]
}

const testAdd = [
    {
        id: 3,
        desc: 'Buy 3',
        amount: 500
    }
]

function reducer(state = initValue, action) {

    switch (action.type) {
        case 'budget/add': {
            return {
                ...state,
                listData: [
                    ...state.listData,
                    {
                        id: action.id,
                        desc: action.desc,
                        amount: action.amount
                    }
                ]
            }
        }
    }
}

export default reducer
