import { createSlice } from '@reduxjs/toolkit'

const taskSlice = createSlice({
    name: "list",
    initialState: [
        {
            desc: 'hi redux',
            amount: -5000000
        },
        {
            desc: 'hi react',
            amount: 3000000
        },
    ],
    reducers: {
        addItem: (state) => {
            return {
                ...state,
            }
        }
    }
})

export const { addItem } = taskSlice.actions

export default taskSlice.reducer;
