// import store  from "./store"

const initValue = {
    listData: [
        {
            id: 1,
            desc: 'Buy',
            amount: 300
        },
        {
            id: 2,
            desc: 'Buy 2',
            amount: -400
        },
    ]
}

function reducer(state = initValue, action) {

    switch (action.type) {
        case 'budget/add': {
            return {
                ...state,
                listData: [
                    ...state.listData,
                    {
                        id: action.id,
                        desc: action.desc,
                        amount: action.amount
                    }
                ]
            }
        }

        default: return state
    }
}



export default reducer
