import cls from 'classnames'
import { useSelector } from 'react-redux'
// import {useState} from 'react'

function BudgetType({
  type,
}) {

  let budgetTypeClass = cls('clearfix', {
    'budget__income': type === 'inc',
    'budget__expenses': type === 'exp',
  })

  const listData = useSelector((state) => state.listData)

  const totalIncome = listData.filter((item) => item.amount > 0).reduce((prev, cur) => prev + cur.amount, 0)

  const totaltExpenses = listData.reduce((prev, cur) => prev + cur.amount < 0 ? cur.amount : 0, 0)

  console.log(totalIncome)

  return (
    <div className={budgetTypeClass}>
      <div className="budget__income--text">{type === 'inc' ? 'Income' : 'Expenses'}</div>
      <div className="right">
        <div className="budget__income--value">{type === 'inc' ? totalIncome : totaltExpenses}</div>
        <div className="budget__income--percentage">45%</div>
      </div>
    </div>
  )
}

export default BudgetType
