// import store  from "./store"

const initValue = {
    addOrEdit: 'add',
    editData: {
        id: '',
        desc: '',
        amount: action.amount,
        addType: action.addType
    },
    listData: [
        // {
        //     id: 1,
        //     desc: 'Buy',
        //     amount: 300
        // },
        // {
        //     id: 2,
        //     desc: 'Buy 2',
        //     amount: -400
        // },
    ]
}

function reducer(state = initValue, action) {

    switch (action.type) {
        case 'budget/add': {
            return {
                ...state,
                listData: [
                    ...state.listData,
                    {
                        id: action.id,
                        addType: action.addType,
                        desc: action.desc,
                        amount: action.addType === 'inc' ? action.amount * 1 : action.amount * (-1)
                    }
                ]
            }
        }
        case 'budget/delete': {
            return {
                ...state,
                listData: [
                    ...state.listData.filter((item) => item.id !== action.id)
                ]
            }
        }
        case 'budget/sendEdit': {
            return {
                ...state,
                ...state.addOrEdit,
                addOrEdit: action.addOrEdit,
                ...state.editData,
                editData: {
                    id: action.id,
                    desc: action.desc,
                    amount: action.amount,
                    addType: action.addType
                },
                listData: [
                    ...state.listData
                ]
            }
        }
        // case 'budget/edit': {
