import { useState } from 'react'
import { useSelector ,useDispatch } from 'react-redux'
import {v4 as uuidv4 }  from 'uuid'

function Add() {

    const [desc, setDesc] = useState('');
    const [amount, setAmount] = useState('');
    const [addType, setAddType] = useState('inc');

    const addOrEdit = useSelector(state => state.addOrEdit)
    const editData = useSelector(state => state.editData)

    const dispatch = useDispatch();

    const handleBtn = () => {
        
        if (addOrEdit === 'add') {
            dispatch({
                type: 'budget/add',
                id: uuidv4(),
                addType,
                desc,
                amount
            })
        } else {
            dispatch({
                type: 'budget/edit',
                addType,
                desc,
                amount
            })
        }
    }

    const handleDesc = (e) => {
        setDesc(e.target.value)
    }

    const handleAddType = (e) => {
        e.target.value === 'inc' ? setAddType('inc') : setAddType('exp')
    }

    // const handleAmount = (e) => {
    //     addType === 'inc' ? setAmount(e.target.value) : setAmount(-e.target.value)
    // }

    

    const handleAmount = (e) => {
        setAmount(e.target.value)
    }

    // const handleEditSelect = () => {
    //     // Object.keys(sendEditData).length > 0 && sendEditData.addOrEdit === '' ? 'inc' : 'exp'}
    //     if (Object.keys(editData).length > 0) {
    //         if (editData.addType === 'inc') {
    //             return editData.addType
    //         } else 
    //             return editData.addType
    //     } else {
    //         return 'inc'
    //     }
    // }

    // let selectVal = handleEditSelect();

    // console.log(handleEditSelect())
    // console.log(editData.addType)
    // console.log(editData.desc)
    // console.log(editData.amount)
    
    return (
        <div className="add">
            <div className="add__container">
                <select className="add__type" onChange={handleAddType} de>
                    <option value="inc">+</option>
                    <option value="exp">-</option>
                </select>
                <input type="text" className="add__description" placeholder="Add description" onChange={handleDesc} defaultValue={editData.desc} />
                <input type="number" className="add__value" placeholder="Value" onChange={handleAmount} defaultValue={editData.amount} />
                <button className="add__btn" onClick={handleBtn}><i className="ion-ios-checkmark-outline" /></button>
            </div>
        </div>
    )
}

export default Add
