import { createSlice } from '@reduxjs/toolkit'

export const taskSlice = createSlice({
    name: "list",
    initialState: [
        {
            desc: 'hi redux',
            amount: -5000000
        },
        {
            desc: 'hi react',
            amount: 3000000
        },
    ],
    reducers: {
        addItem: (state, action) => {
            const newTask = {
                desc: action.payload
            }
        }
    }
})

export const { addItem } = taskSlice.actions
