function reducer(state = initValue, action) {

    switch (action.type) {
        case 'budget/add': {
            return {
                ...state,
                listData: [
                    ...state.listData,
                    {
                        id: action.id,
                        desc: action.desc,
                        amount: action.amount
                    }
                ]
            }
        }
    }
}

export default reducer
