import { createStore } from 'redux'
// import reducer from './reducer'


const initValue = {
    listData: [
        {
            id: 1,
            desc: 'Buy',
            amount: 300
        },
        {
            id: 2,
            desc: 'Buy 2',
            amount: -400
        },
    ]
}

const testAdd = {
    type: 'budget/add',
    id: 3,
    desc: 'Buy 3',
    amount: 500
}

function reducer(state = initValue, action) {

    switch (action.type) {
        case 'budget/add': {
            return {
                ...state,
                listData: [
                    ...state.listData,
                    {
                        id: action.id,
                        desc: action.desc,
                        amount: action.amount
                    }
                ]
            }
        }
    }
}



const store = legacy_createStore(reducer)

// Testing
console.log('Test add', store.dispatch())
console.log('Get state', store.getState())


export default store
