import { useState } from 'react'
import { useSelector ,useDispatch } from 'react-redux'
import {v4 as uuidv4 }  from 'uuid'

function Add() {

    const [desc, setDesc] = useState('');
    const [amount, setAmount] = useState('');
    const [addType, setAddType] = useState('inc');

    const addOrEdit = useSelector(state => state.addOrEdit)
    const sendEditData = useSelector(state => state.sendEditData)

    


    const dispatch = useDispatch();

    const handleBtn = () => {
        
        if (addOrEdit === 'add') {
            dispatch({
                type: 'budget/add',
                id: uuidv4(),
                addType,
                desc,
                amount
            })
        } else {
            dispatch({
                type: 'budget/edit',
                addType,
                desc,
                amount
            })
        }
    }

    const handleDesc = (e) => {
        setDesc(e.target.value)
    }

    const handleAddType = (e) => {
        e.target.value === 'inc' ? setAddType('inc') : setAddType('exp')
    }

    // const handleAmount = (e) => {
    //     addType === 'inc' ? setAmount(e.target.value) : setAmount(-e.target.value)
    // }

    

    const handleAmount = (e) => {
        setAmount(e.target.value)
    }

    const handleEditSelect = () => {
        // Object.keys(sendEditData).length > 0 && sendEditData.addOrEdit === '' ? 'inc' : 'exp'}
        if (Object.keys(sendEditData).length > 0) {
            if (sendEditData.addType === 'inc') {
                return sendEditData.addType
            } else 
                return sendEditData.addType
        } else {
            return 'inc'
        }
    }

    let selectVal = handleEditSelect();
    
    return (
        <div className="add">
            <div className="add__container">
                <select className="add__type" onChange={handleAddType} defaultValue='exp'>
                    <option value="inc">+</option>
                    <option value="exp">-</option>
                </select>
                <input type="text" className="add__description" placeholder="Add description" onChange={handleDesc}/>
                <input type="number" className="add__value" placeholder="Value" onChange={handleAmount} />
                <button className="add__btn" onClick={handleBtn}><i className="ion-ios-checkmark-outline" /></button>
            </div>
        </div>
    )
}

export default Add
